schema:
{
 "description": "AuthService is the Schema for the authservices API",
 "properties": {
  "spec": {
   "description": "AuthServiceSpec defines the desired state of AuthService",
   "properties": {
    "add_auth_headers": {
     "additionalProperties": {
      "description": "BoolOrString is a type that can hold a Boolean or a string.",
      "oneOf": [
       {
        "type": "string"
       },
       {
        "type": "boolean"
       }
      ]
     },
     "type": "object"
    },
    "add_linkerd_headers": {
     "type": "boolean"
    },
    "allow_request_body": {
     "type": "boolean"
    },
    "allowed_authorization_headers": {
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "allowed_request_headers": {
     "items": {
      "type": "string"
     },
     "type": "array"
    },
    "ambassador_id": {
     "description": "AmbassadorID declares which Ambassador instances should pay attention to this resource.  May either be a string or a list of strings.  If no value is provided, the default is: \n    ambassador_id:    - \"default\"",
     "items": {
      "type": "string"
     },
     "oneOf": [
      {
       "type": "string"
      },
      {
       "type": "array"
      }
     ]
    },
    "auth_service": {
     "type": "string"
    },
    "failure_mode_allow": {
     "type": "boolean"
    },
    "include_body": {
     "properties": {
      "allow_partial": {
       "type": "boolean"
      },
      "max_bytes": {
       "description": "These aren't pointer types because they are required.",
       "type": "integer"
      }
     },
     "required": [
      "allow_partial",
      "max_bytes"
     ],
     "type": "object"
    },
    "path_prefix": {
     "type": "string"
    },
    "proto": {
     "enum": [
      "http",
      "grpc"
     ],
     "type": "string"
    },
    "protocol_version": {
     "enum": [
      "v2",
      "v3"
     ],
     "type": "string"
    },
    "status_on_error": {
     "description": "Why isn't this just an int??",
     "properties": {
      "code": {
       "type": "integer"
      }
     },
     "type": "object"
    },
    "timeout_ms": {
     "type": "integer"
    },
    "tls": {
     "description": "BoolOrString is a type that can hold a Boolean or a string.",
     "oneOf": [
      {
       "type": "string"
      },
      {
       "type": "boolean"
      }
     ]
    }
   },
   "required": [
    "auth_service"
   ],
   "type": "object"
  }
 },
 "title": "Auth Service",
 "type": "object"
}
