schema:
{
 "description": "LogService is the Schema for the logservices API",
 "properties": {
  "spec": {
   "description": "LogServiceSpec defines the desired state of LogService",
   "properties": {
    "ambassador_id": {
     "description": "AmbassadorID declares which Ambassador instances should pay attention to this resource.  May either be a string or a list of strings.  If no value is provided, the default is: \n    ambassador_id:    - \"default\"",
     "items": {
      "type": "string"
     },
     "oneOf": [
      {
       "type": "string"
      },
      {
       "type": "array"
      }
     ]
    },
    "driver": {
     "enum": [
      "tcp",
      "http"
     ],
     "type": "string"
    },
    "driver_config": {
     "properties": {
      "additional_log_headers": {
       "items": {
        "properties": {
         "during_request": {
          "type": "boolean"
         },
         "during_response": {
          "type": "boolean"
         },
         "during_trailer": {
          "type": "boolean"
         },
         "header_name": {
          "type": "string"
         }
        },
        "type": "object"
       },
       "type": "array"
      }
     },
     "type": "object"
    },
    "flush_interval_byte_size": {
     "type": "integer"
    },
    "flush_interval_time": {
     "type": "integer"
    },
    "grpc": {
     "type": "boolean"
    },
    "service": {
     "type": "string"
    }
   },
   "type": "object"
  }
 },
 "title": "Log Service",
 "type": "object"
}
