schema:
{
 "description": "StorageNode is the Schema for the ShardingSphere storage unit",
 "properties": {
  "spec": {
   "description": "StorageNodeSpec defines the desired state of a set of storage units",
   "properties": {
    "replicas": {
     "default": 1,
     "description": "Only for aws aurora storage provider right now. And the default value is 1. aws rds instance is always 1. aws rds cluster will auto create 3 instances(1 primary and 2 replicas).",
     "format": "int32",
     "type": "integer"
    },
    "schema": {
     "description": "the default database name of the storage node. if not set, will NOT create database",
     "type": "string"
    },
    "storageProviderName": {
     "type": "string"
    }
   },
   "required": [
    "storageProviderName"
   ],
   "type": "object"
  }
 },
 "title": "Storage Node",
 "type": "object"
}
