schema:
{
 "description": "PostgreSQLDatabase is the Schema for the postgresqls API",
 "properties": {
  "spec": {
   "description": "PostgreSQLDatabaseSpec defines the desired state of PostgreSQLDatabase",
   "properties": {
    "address": {
     "description": "The connect URI",
     "type": "string"
    },
    "databaseName": {
     "description": "DatabaseName is by default the same as metata.name",
     "type": "string"
    },
    "extensions": {
     "description": "Database extensions",
     "items": {
      "description": "Extension is a resource representing database extension",
      "properties": {
       "name": {
        "type": "string"
       }
      },
      "required": [
       "name"
      ],
      "type": "object"
     },
     "type": "array"
    },
    "rootSecret": {
     "description": "Contains a credentials set of a user with enough permission to manage databases and user accounts",
     "properties": {
      "name": {
       "description": "Name referrs to the name of the secret, must be located whithin the same namespace",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace, by default the same namespace is used.",
       "type": "string"
      },
      "passwordField": {
       "default": "password",
       "type": "string"
      },
      "userField": {
       "default": "username",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    }
   },
   "required": [
    "rootSecret"
   ],
   "type": "object"
  }
 },
 "title": "Postgre SQL Database",
 "type": "object"
}
