schema:
{
 "description": "PostgreSQLUser is the Schema for the mongodbs API",
 "properties": {
  "spec": {
   "properties": {
    "credentials": {
     "description": "SecretReference is a named reference to a secret which contains user credentials",
     "properties": {
      "name": {
       "description": "Name referrs to the name of the secret, must be located whithin the same namespace",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace, by default the same namespace is used.",
       "type": "string"
      },
      "passwordField": {
       "default": "password",
       "type": "string"
      },
      "userField": {
       "default": "username",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    },
    "database": {
     "description": "DatabaseReference is a named reference to a database kind",
     "properties": {
      "name": {
       "description": "Name referrs to the name of the database kind, mist be located within the same namespace",
       "type": "string"
      }
     },
     "required": [
      "name"
     ],
     "type": "object"
    }
   },
   "required": [
    "credentials",
    "database"
   ],
   "type": "object"
  }
 },
 "title": "Postgre SQL User",
 "type": "object"
}
