schema:
{
 "description": "SinkBinding describes a Binding that is also a Source. The `sink` (from the Source duck) is resolved to a URL and then projected into the `subject` by augmenting the runtime contract of the referenced containers to have a `K_SINK` environment variable holding the endpoint to which to send cloud events.",
 "properties": {
  "spec": {
   "properties": {
    "ceOverrides": {
     "description": "CloudEventOverrides defines overrides to control the output format and modifications of the event sent to the sink.",
     "properties": {
      "extensions": {
       "description": "Extensions specify what attribute are added or overridden on the outbound event. Each `Extensions` key-value pair are set on the event as an attribute extension independently.",
       "format": "textarea",
       "type": "string"
      }
     },
     "type": "object"
    },
    "sink": {
     "description": "Sink is a reference to an object that will resolve to a uri to use as the sink.",
     "properties": {
      "ref": {
       "description": "Ref points to an Addressable.",
       "properties": {
        "apiVersion": {
         "description": "API version of the referent.",
         "type": "string"
        },
        "kind": {
         "description": "Kind of the referent. More info: https://git.k8s.io/community/contributors/devel/sig-architecture/api-conventions.md#types-kinds",
         "type": "string"
        },
        "name": {
         "description": "Name of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/names/#names",
         "type": "string"
        },
        "namespace": {
         "description": "Namespace of the referent. More info: https://kubernetes.io/docs/concepts/overview/working-with-objects/namespaces/ This is optional field, it gets defaulted to the object holding it if left out.",
         "type": "string"
        }
       },
       "type": "object"
      },
      "uri": {
       "description": "URI can be an absolute URL(non-empty scheme and non-empty host) pointing to the target or a relative URI. Relative URIs will be resolved using the base URI retrieved from Ref.",
       "type": "string"
      }
     },
     "type": "object"
    },
    "subject": {
     "description": "Subject references the resource(s) whose \"runtime contract\" should be augmented by Binding implementations.",
     "properties": {
      "apiVersion": {
       "description": "API version of the referent.",
       "type": "string"
      },
      "kind": {
       "description": "Kind of the referent.",
       "type": "string"
      },
      "name": {
       "description": "Name of the referent. Mutually exclusive with Selector.",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the referent.",
       "type": "string"
      },
      "selector": {
       "description": "Selector of the referents. Mutually exclusive with Name.",
       "properties": {
        "matchExpressions": {
         "description": "matchExpressions is a list of label selector requirements. The requirements are ANDed.",
         "items": {
          "properties": {
           "key": {
            "description": "key is the label key that the selector applies to.",
            "type": "string"
           },
           "operator": {
            "description": "operator represents a key's relationship to a set of values. Valid operators are In, NotIn, Exists and DoesNotExist.",
            "type": "string"
           },
           "values": {
            "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty. This array is replaced during a strategic merge patch.",
            "items": {
             "type": "string"
            },
            "type": "array"
           }
          },
          "type": "object"
         },
         "type": "array"
        },
        "matchLabels": {
         "description": "matchLabels is a map of {key,value} pairs. A single {key,value} in the matchLabels map is equivalent to an element of matchExpressions, whose key field is \"key\", the operator is \"In\", and the values array contains only \"value\". The requirements are ANDed.",
         "format": "textarea",
         "type": "string"
        }
       },
       "type": "object"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Sink Binding",
 "type": "object"
}
