schema:
{
 "properties": {
  "spec": {
   "properties": {
    "directives": {
     "properties": {
      "authProxy": {
       "description": "Add auth proxy header to requests",
       "type": "boolean"
      },
      "changeOrigin": {
       "description": "Change Host header for name-based virtual hosted sites.",
       "type": "boolean"
      },
      "headerDown": {
       "description": "Sets, adds (with the + prefix), deletes (with the - prefix), or performs a replacement (by using two arguments, a search and replacement) in a response header coming downstream from the backend.",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "headerUp": {
       "description": "Sets, adds (with the + prefix), deletes (with the - prefix), or performs a replacement (by using two arguments, a search and replacement) in a request header going upstream to the backend.",
       "items": {
        "type": "string"
       },
       "type": "array"
      },
      "interceptRedirects": {
       "description": "InterceptRedirects determines whether the proxy should sniff backend responses for redirects, only allows redirects to the same host. Deprecated, https://github.com/kubernetes/kubernetes/pull/106830",
       "type": "boolean"
      },
      "method": {
       "description": "Changes the request's HTTP verb.",
       "type": "string"
      },
      "stripPathPrefix": {
       "description": "Strips the given prefix from the beginning of the URI path.",
       "type": "string"
      },
      "stripPathSuffix": {
       "description": "Strips the given suffix from the end of the URI path.",
       "type": "string"
      },
      "wrapTransport": {
       "description": "WrapTransport indicates whether the provided Transport should be wrapped with default proxy transport behavior (URL rewriting, X-Forwarded-* header setting)",
       "type": "boolean"
      }
     },
     "type": "object"
    },
    "matcher": {
     "properties": {
      "method": {
       "type": "string"
      },
      "path": {
       "type": "string"
      }
     },
     "required": [
      "method",
      "path"
     ],
     "type": "object"
    },
    "upstream": {
     "properties": {
      "caBundle": {
       "format": "byte",
       "type": "string"
      },
      "insecureSkipVerify": {
       "type": "boolean"
      },
      "service": {
       "description": "service is a reference to the service for this endpoint. Either service or url must be specified. the scheme is default to HTTPS.",
       "properties": {
        "name": {
         "description": "name is the name of the service. Required",
         "type": "string"
        },
        "namespace": {
         "description": "namespace is the namespace of the service. Required",
         "type": "string"
        },
        "path": {
         "description": "path is an optional URL path at which the upstream will be contacted.",
         "type": "string"
        },
        "port": {
         "description": "port is an optional service port at which the upstream will be contacted. `port` should be a valid port number (1-65535, inclusive). Defaults to 443 for backward compatibility.",
         "format": "int32",
         "type": "integer"
        }
       },
       "required": [
        "name",
        "namespace"
       ],
       "type": "object"
      },
      "url": {
       "description": "`url` gives the location of the upstream, in standard URL form (`scheme://host:port/path`). Exactly one of `url` or `service` must be specified.",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "type": "object"
  }
 },
 "title": "Reverse Proxy",
 "type": "object"
}
