schema:
{
 "description": "SMTPServer is the Schema for the smtpserver API",
 "properties": {
  "spec": {
   "properties": {
    "authType": {
     "enum": [
      "PLAIN",
      "LOGIN",
      "CRAM-MD5",
      "SCRAM-SHA-1"
     ],
     "type": "string"
    },
    "connect": {
     "enum": [
      "plain",
      "ssl",
      "starttls"
     ],
     "type": "string"
    },
    "port": {
     "type": "integer"
    },
    "server": {
     "type": "string"
    },
    "tls": {
     "description": "DEPRECATED: Use Connect, instead",
     "type": "boolean"
    }
   },
   "required": [
    "server"
   ],
   "type": "object"
  }
 },
 "title": "SMTP Server",
 "type": "object"
}
