schema:
{
 "description": "Platform is the Schema for the Platforms API",
 "properties": {
  "spec": {
   "description": "PlatformSpec defines the desired state of Platform",
   "properties": {
    "imagePullPolicy": {
     "type": "string"
    },
    "initContainerImage": {
     "type": "string"
    },
    "serviceAccount": {
     "type": "string"
    },
    "services": {
     "items": {
      "description": "A service object",
      "properties": {
       "secretRefs": {
        "items": {
         "description": "SecretRef is a reference to an existing secret that will be mounted on a resource",
         "properties": {
          "mountPath": {
           "type": "string"
          },
          "name": {
           "type": "string"
          }
         },
         "required": [
          "name"
         ],
         "type": "object"
        },
        "type": "array"
       },
       "type": {
        "description": "node type: elastic_input",
        "type": "string"
       },
       "url": {
        "type": "string"
       }
      },
      "required": [
       "secretRefs",
       "type"
      ],
      "type": "object"
     },
     "type": "array"
    }
   },
   "required": [
    "serviceAccount",
    "services"
   ],
   "type": "object"
  }
 },
 "title": "Platform",
 "type": "object"
}
