      "password": {
       "description": "Password for the sql user",
       "type": "string"
      },
      "username": {
       "description": "Username for the sql user",
       "type": "string"
      }
     },
     "type": "object"
    },
    "hostRef": {
     "description": "Reference to the SqlHost",
     "properties": {
      "name": {
       "description": "Name of the SqlObject",
       "type": "string"
      },
      "namespace": {
       "description": "Namespace of the SqlObject",
       "type": "string"
      }
     },
     "type": "object"
    }
   },
   "required": [
    "hostRef"
   ],
   "type": "object"
  }
 },
