schema:
{
 "properties": {
  "clusterWide": {
   "description": "Selector for cluster wide scan.",
   "type": "boolean"
  },
  "crontab": {
   "description": "Crontab format expression for scheduling scans.",
   "pattern": "^(\\d+|\\*)(/\\d+)?(\\s+(\\d+|\\*)(/\\d+)?){4}$",
   "type": "string"
  },
  "image_pull_secrets": {
   "description": "List of image pull secret names in the operator's namespace for image pulls.",
   "items": {
    "type": "string"
   },
   "type": "array"
  },
  "integrations": {
   "properties": {
    "defectdojo": {
     "description": "DefectDojo integration options",
     "properties": {
      "api_key": {
       "description": "Api key for DefectDojo api authentication.",
       "type": "string"
      },
      "host": {
       "description": "URL of the DefectDojo server.",
       "type": "string"
      },
      "k8s-cluster-name": {
       "description": "Name of the Kubernetes Cluster in defectdojo.",
       "type": "string"
      }
     },
     "type": "object"
    },
    "policyreport": {
     "description": "Selector to enable policy report object creation and integration wit plicy-reporter.",
     "type": "boolean"
    }
   },
   "type": "object"
  },
  "namespace_selector": {
   "description": "NamespaceSelector selects the namespaces to scan images.",
   "type": "string"
  },
  "spec": {
   "format": "textarea",
   "type": "string"
  }
 },
 "title": "Namespace Scanner",
 "type": "object"
}
